settings.custom_liquid:
{% comment %}
  Add this code to your product.liquid file, just before the closing </body> tag
{% endcomment %}

{% assign inventory_quantities = "" %}
{% for variant in product.variants %}
  {% if variant.inventory_quantity <= 10 and variant.inventory_quantity > 0 %}
    {% assign inventory_quantities = inventory_quantities | append: variant.id | append: ":" | append: variant.inventory_quantity | append: "," %}
  {% endif %}
{% endfor %}

<style>
  #foxkit-stock-countdown {
    margin-top: 15px;
    margin-bottom: 15px;
    opacity: 0;
    transition: opacity 0.5s ease-out;
  }
  .foxkit-stock-countdown__message {
    margin-bottom: 5px;
    font-size: 14px;
  }
  .foxkit-stock-countdown__progress-container {
    background-color: #e0e0e0;
    height: 8px;
    border-radius: 4px;
    overflow: hidden;
  }
  .foxkit-stock-countdown__progress-bar {
    height: 100%;
    width: 100%;
    transition: background-color 0.5s;
    animation: dropAnimation 1s ease-out forwards;
  }
  @keyframes dropAnimation {
    0% { width: 100%; }
    100% { width: var(--final-width); }
  }
</style>

<div id="foxkit-stock-countdown" style="display: none;">
  <div class="foxkit-stock-countdown__message">Only <strong class="data-stock-number"></strong> item(s) left in stock!</div>
  <div class="foxkit-stock-countdown__progress-container">
    <div class="foxkit-stock-countdown__progress-bar"></div>
  </div>
</div>

<script>
  var inventoryData = {};
  "{{ inventory_quantities }}".split(',').forEach(function(item) {
    if (item) {
      var parts = item.split(':');
      inventoryData[parts[0]] = parseInt(parts[1]);
    }
  });

  var stockCountdown = document.getElementById('foxkit-stock-countdown');
  var stockNumber = stockCountdown.querySelector('.data-stock-number');
  var progressBar = stockCountdown.querySelector('.foxkit-stock-countdown__progress-bar');

  function updateStockCountdown(variantId) {
    var quantity = inventoryData[variantId];
    if (quantity !== undefined) {
      stockNumber.textContent = quantity;
      var percentage = quantity * 10;

      progressBar.style.setProperty('--final-width', percentage + '%');
      progressBar.style.animation = 'none';
      progressBar.offsetHeight; // Trigger reflow
      progressBar.style.animation = 'dropAnimation 1s ease-out forwards';

      if (quantity <= 3) {
        progressBar.style.backgroundColor = '#ff3b30';
      } else if (quantity <= 7) {
        progressBar.style.backgroundColor = '#ff9500';
      } else {
        progressBar.style.backgroundColor = '#34c759';
      }

      stockCountdown.style.display = 'block';
      setTimeout(function() {
        stockCountdown.style.opacity = '1';
      }, 10);
    } else {
      stockCountdown.style.display = 'none';
      stockCountdown.style.opacity = '0';
    }
  }

  // Initial update
  updateStockCountdown("{{ product.selected_or_first_available_variant.id }}");

  // Listen for variant changes
  document.addEventListener('change', function(event) {
    if (event.target.matches('select[name="id"], input[name="id"]')) {
      updateStockCountdown(event.target.value);
    }
  });

  // Listen for variant:change event (used by some themes)
  document.addEventListener('variant:change', function(event) {
    if (event.detail && event.detail.variant) {
      updateStockCountdown(event.detail.variant.id);
    }
  });
</script>

{% if product %}
  {% assign variant_sku_values = product.variants | map: 'sku' %}
  {% assign sku_values = variant_sku_values | uniq | join: ';' %}
  {% assign ls_selected_or_first_variant = product.selected_or_first_available_variant %}

  {% if product.available %}
    {% assign ls_availability = 1 %}
  {% else %}
    {% assign ls_availability = 0 %}
  {% endif %}

  <div id="lipscore-review-list"
    ls-product-name="{{ product.title }}"
    ls-brand="{{ product.vendor }}"
    ls-sku="{{ sku_values }}"
    ls-product-id="{{ product.id }}"
    ls-product-url="{{ shop.url }}{{ product.url }}"
    ls-description="{{ product.description | escape }}"
    ls-image-url="{{ product | img_url: '1024x1024' | remove: '_1024x1024' }}"
    ls-price="{{ ls_selected_or_first_variant.price | money_without_currency }}"
    ls-price-currency="{{ shop.currency }}"
    ls-availability="{{ ls_availability }}"
    ls-category="{{ product.type }}"></div>
{% endif %}
